Registro.Script:
CREATE TABLE IF NOT EXISTS `rhversaocvweb` (
  `VersaoSistema` varchar(12) NOT NULL,
  `DataVersao` date DEFAULT NULL,
  `UltimoScript` datetime DEFAULT NULL,  
  PRIMARY KEY (`VersaoSistema`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1 COMMENT='Versão do Currículo Web';

CREATE TABLE IF NOT EXISTS `rhcamposwebobr` (
  `Empresa` varchar(4) NOT NULL,
  `IdCampoObr` int(3) NOT NULL,
  PRIMARY KEY (`Empresa`, `IdCampoObr`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 COMMENT='Campos Obrigatorios';

CREATE TABLE IF NOT EXISTS `rhrequisitoswebobr` (
  `Empresa` varchar(4) NOT NULL,
  `Requisito` varchar(4) NOT NULL,
  PRIMARY KEY (`Empresa`, `Requisito`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 COMMENT='Requisitos Obrigatorios';

CREATE TABLE IF NOT EXISTS `rhvariaveiswebobr` (
  `Empresa` varchar(4) NOT NULL,
  `Variavel` varchar(4) NOT NULL,
  PRIMARY KEY (`Empresa`, `Variavel`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 COMMENT='Dados Complementares Obrigatorios';

CREATE TABLE IF NOT EXISTS `rhtiposcurso` (
  `TipoCurso` varchar(2) NOT NULL,
  `Descricao40` varchar(40) DEFAULT NULL,
  PRIMARY KEY (`TipoCurso`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 COMMENT='Tipos de Curso';

CREATE TABLE IF NOT EXISTS `rhdiretrizescvwebdef` (
  `DiretrizWeb` varchar(40) NOT NULL,
  `Descricao80` varchar(80) DEFAULT NULL,
  `TipoDiretrizWeb` char(1) DEFAULT NULL,  
  PRIMARY KEY (`DiretrizWeb`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1 COMMENT='Diretrizes do Currículo Web';

CREATE TABLE IF NOT EXISTS `rhdiretrizescvwebopc` (
  `DiretrizWeb` varchar(40) NOT NULL,
  `NroOrdem` int(2) NOT NULL,
  `Descricao40` varchar(40) DEFAULT NULL,  
  PRIMARY KEY (`DiretrizWeb`, `NroOrdem`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1 COMMENT='Opções das Diretrizes do Currículo Web';

CREATE TABLE IF NOT EXISTS `rhpessoasfotos` (
  `Empresa` varchar(4) NOT NULL,
  `Pessoa` int(8) NOT NULL,
  `Foto` blob,
  PRIMARY KEY (`Empresa`, `Pessoa`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1 COMMENT='Fotos das Pessoas';

CREATE TABLE IF NOT EXISTS rhdiagnosticos (
  `Id` int(3) NOT NULL,
  `Log` varchar(4000) DEFAULT NULL,
  `Tipo` varchar(1) DEFAULT NULL,
  PRIMARY KEY (`Id`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 COMMENT='Tabela de Log de Diagnósticos';

CREATE TABLE IF NOT EXISTS `rhregioes` ( `Regiao` varchar(4) NOT NULL,
 `Descricao60` varchar(60) NULL,
 `AtivaDesativada` CHAR(1) NULL,
 PRIMARY KEY (`Regiao`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1 COMMENT='Regiões';

CREATE TABLE IF NOT EXISTS `rhcamposwebinv` (
  `Empresa` varchar(4) NOT NULL,
  `IdCampoInv` int(3) NOT NULL,
  PRIMARY KEY (`Empresa`, `IdCampoInv`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 COMMENT='Campos Ocultos';
